default
{
	state_entry()
	{
		llSay(PUBLIC_CHANNEL, "Hello, Avatar!");
	}

	touch_start(integer num_detected)
	{
		llSay(PUBLIC_CHANNEL, "Touched.");
	}
}

default
{
	state_entry()
	{
		llSetText(${1:string text}, ${2:vector color}, ${3:float alpha});
		llRemoveInventory(llGetScriptName());
	}
}

default
{
	touch_start(integer num_detected)
	{
		llMapDestination(${1:string simname}, ${2:vector pos}, ZERO_VECTOR);
	}
}

default
{
	changed(integer change)
	{
		if (change & CHANGED_OWNER)
			llResetScript();
	}

	state_entry()
	{
		llSetPayPrice(PAY_DEFAULT, [PAY_HIDE, PAY_HIDE, PAY_HIDE, PAY_HIDE]);
	}

	money(key id, integer amount)
	{
		llInstantMessage(id, "Thank you, " + llKey2Name(id) + ".");
	}
}

string menuDialog = "\nSome info text for the menu!";
list menuButtons = ["left", "middle", "right"];

integer dialogChannel;
integer dialogHandle;

open_menu(key inputKey, string inputString, list inputList)
{
	dialogChannel = (integer)llFrand(DEBUG_CHANNEL)*-1;
	dialogHandle = llListen(dialogChannel, "", inputKey, "");
	llDialog(inputKey, inputString, inputList, dialogChannel);
	llSetTimerEvent(30.0);
}

close_menu()
{
	llSetTimerEvent(0.0);
	llListenRemove(dialogHandle);
	dialogHandle = FALSE;
	dialogChannel = (integer)llFrand(DEBUG_CHANNEL)*-1;
}

default
{
	on_rez(integer start_param)
	{
		llResetScript();
	}

	touch_start(integer total_number)
	{
		open_menu(llDetectedKey(0), menuDialog, menuButtons);
	}

	listen(integer channel, string name, key id, string message)
	{
		if(channel != dialogChannel)
			return;
		close_menu();

		if (message == "middle")
		{
			llRegionSayTo(id, PUBLIC_CHANNEL, "Hello there, you clicked the middle button!");
		}
	}

	timer()
	{
		close_menu();
	}
}

string first_name_of_avatar(key inputKey)
{
	string fullName = llKey2Name(inputKey);
	integer positionOfLastCharacterInFirstName = llSubStringIndex(fullName, " ") - 1;

	string firstName = llGetSubString(fullName, 0, positionOfLastCharacterInFirstName);

	return firstName;
}

default
{
	touch_start(integer num_detected)
	{
		key id = llDetectedKey(0);

		llRegionSayTo(id, PUBLIC_CHANNEL, "Hello, " + first_name_of_avatar(id) + ".");
	}
}
